export default class RegisterController {

    name = String;
    email = String;
    senha = String;
    confSenha = String;
    ErroName = String;
    ErroEmail = String;
    ErroSenha = String;
    ErroConfirmeSenha = String;
    ErroSenhaDiferente = String;
    flag = Boolean;


    constructor(){
        this.ErroName = null;
        this.name = null;
        this.email = null;
        this.senha = null;
        this.ErroEmail = null;
        this.ErroSenha = null;
        this.ErroConfirmeSenha = null;
        this.flag = true;
        this.ErroSenhaDiferente = null;
