            this.ErroSenha = "Preencha o campo senha";
            this.ErroConfirmeSenha = "Preencha o campo confirmação de senha";
            this.flag = false
            return;
        }
        if(this.ErroName === null ){
           this.ErroName = "Preencha o campo nome";
           this.flag = false;
        }
        if(this.email === null){
            this.ErroEmail = "Preencha o campo email";
            this.flag = false;
        }
        if(this.senha === null){
            this.ErroSenha = "Preencha o campo senha";
            this.flag = false;
        }
        if(this.ErroConfirmeSenha === null ){
           this.ErroConfirmeSenha = "Preencha o campo comfirme senha";
           this.flag = false;
        }

        if(this.flag === true && senha === confSenha)
        {
            return flag;
        }
        else if(!(senha === confSenha)){

            this.ErroSenhaDiferente = "Senhas diferentes";
        }
        else if(this.flag == false) return;
   }

   registerUser(name, email, senha, confSenha)
   {
        this.validaInputs(name, email, senha, confSenha);
