import React, {useState, useEffect} from 'react';
import {
    StyleSheet,
    View, 
    Text, 
    TextInput, 
    TouchableOpacity, 
    Button,
    ActivityIndicator,
    ScrollView,
    Image,
    Platform,
} from 'react-native';
import Constants from 'expo-constants';
import * as ImagePicker from 'expo-image-picker';
import styles from '../../assets/style';

import * as SQLite from 'expo-sqlite';
const db = SQLite.openDatabase("database.db");

export default function Novo({navigation})
{
    //const [image, setImage] = useState(null);
    const [nome, setNome] = useState(null);
    const [descricao, setDescicao] = useState(null);

    return(

        <View style={stylesss.container}>
            <TextInput
                placeholder={'Nome'}
                style={stylesss.input}
                onChangeText={nome => setNome(nome)}
            />

            <TextInput
                placeholder={'Descrição'}
                style={stylesss.input}
                onChangeText={descricao => setDescicao(descricao)}
            />

            <View style={styles.ViewImagem}>
              {image && <Image source={{ uri: image }} style={styles.imagem} />}
            </View>
            <Button title="Carrege sua imagem" onPress={pickImage} />

            <TouchableOpacity 
                    title="Cadastrar"
                    style={styles.ButtonLogin}
                    onPress={() => insertProduto()} 
                    >
                    <Text style={styles.textbuttonLogin}>Cadastrar</Text>
                </TouchableOpacity>
        </View>

    );
}

const stylesss = StyleSheet.create({
    container: {
      flex: 1,
      paddingTop: Constants.statusBarHeight,
      padding: 8,
      margin: 15,
    },
    input: {
      height: 40,
      borderWidth: 1,
      borderColor: '#ccc',
      padding: 10,
      marginVertical: 5,
    },
    textarea: {
      height: 150,
      borderWidth: 1,
      borderColor: '#ccc',
      padding: 10,
      marginVertical: 5,
    },
    ViewImagem: {
      width: 400, 
      height: 400,
      backgroundColor:"beige",
      borderWidth: 2, 
      marginBottom: 20,
      //flex: 0.3,
  },
  imagem: {
      width: 400, 
      height: 400,
      resizeMode: 'stretch'
  }

