import React, {useState} from 'react';
//import axios from "axios";
import api from '../../config/Axios';

export default class UserController {

    email = String;
    senha = String;
    ErroEmail = String;
    ErroSenha = String;
    users = []; 

    constructor()
    {
        this.email = null;
        this.senha = null;
        this.users = [];
        this.ErroEmail = null;
        this.ErroSenha = null;
    }

    validaInputs(email, senha)
    {

        this.email = email;
        this.senha = senha;

        if( this.email === null && this.senha === null){
            this.ErroEmail = "Preencha o campo email";
            this.ErroSenha = "Preencha o campo senha";
            return 0;
        }
        //else {this.loginValidate(); return}

        if(this.email === null)
            this.ErroEmail = "Preencha o campo email";
        if(this.senha === null)
            this.ErroSenha = "Preencha o campo senha";
   }

   getEmail(){return this.email;}

   Allusers()
   {

    api.get("users").then(res => {
      const users = res.data;
      console.log(users);
    });

  }

  user()
  {
    const user = {nome: this.email, senha: this.senha}; 
    api.post("user", {user}).then(res => {
      const user = res.data;
      console.log(user);
    });
  }

  auth()
  {
    api.post("auth", {'email': this.email,'senha':this.senha}).then(res => {
      const user = res.data;
      console.log(user);
    });
  }

}
